function getRandomInt(o){return Math.floor(Math.random()*Math.floor(o))}function arrayCount(o,t){if(!o)return 0;o.forEach(o=>o==t?1:0)}function checkOnDualList(o,t){for(let n=0;n<t.length;n++)if(o===t[n][0])return t[n][2]}function playAudio(o,t=.2,n=!1,r=TIMEOUT_DELAY){let e=new Audio("sounds/"+o+".wav");e.volume=t,e.loop=n,e.muted=!1,e.play(),window.setTimeout(()=>{e.muted=!0,e.loop=!1},r)}function throwError(o){playAudio("GhostSpook",.4),$("#errBlock").html('<i class="fa fa-exclamation-triangle"></i> '+o),$("#errBlock").fadeIn(500).delay(5e3).fadeOut(500,()=>{$("#errBlock").html("&nbsp;")})}function htmlEncode(o){return String(o).replace(/[^\w. ]/gi,function(o){return"&#"+o.charCodeAt(0)+";"})}