function getRandomInt(max){return Math.floor(Math.random()*Math.floor(max))}function arrayCount(haystack,needle){if(!haystack)return 0;haystack.forEach(item=>item==needle?1:0)}function checkOnDualList(needle,haystack){for(let i=0;i<haystack.length;i++)if(needle===haystack[i][0])return haystack[i][2]}function playAudio(fileName,soundLevel=.2,loop=!1,endLoopDelay=TIMEOUT_DELAY){let audio=new Audio("sounds/"+fileName+".wav");audio.volume=soundLevel,audio.loop=loop,audio.muted=!1,audio.play(),window.setTimeout(()=>{audio.muted=!0,audio.loop=!1},endLoopDelay)}function throwError(errText){playAudio("GhostSpook",.4),$("#errBlock").html('<i class="fa fa-exclamation-triangle"></i> '+errText),$("#errBlock").fadeIn(500).delay(5e3).fadeOut(500,()=>{$("#errBlock").html("&nbsp;")})}function htmlEncode(str){return String(str).replace(/[^\w. ]/gi,(function(c){return"&#"+c.charCodeAt(0)+";"}))}